async (page) => {
  try {
    // Get current URL
    const url = page.url();
    const title = await page.title();

    // Get page HTML
    const html = await page.content();

    // Look for railway.app URLs
    const urlRegex = /https:\/\/[a-zA-Z0-9][a-zA-Z0-9.-]*\.(?:up\.)?railway\.app[^\s"'<>]*/g;
    const matches = html.match(urlRegex);
    const uniqueUrls = matches ? [...new Set(matches)] : [];

    // Take screenshot
    await page.screenshot({ path: '/tmp/railway-dashboard.png', fullPage: false });

    // Extract text content to look for service names
    const text = await page.evaluate(() => document.body.innerText);

    return {
      currentUrl: url,
      pageTitle: title,
      foundUrls: uniqueUrls,
      pageText: text.substring(0, 2000)
    };
  } catch (error) {
    return { error: error.message };
  }
}
